mapper.select:
select

from 

where 1=1

	and
		(
		purchase_code = #{pd.purchase_code}
		)


	and (
		status = #{pd.status}
	)


	and (
		status != #{pd.notStatus}
	)


	and (
		supplier_name like  CONCAT('%',#{pd.supplier_name},'%' ) 
	)


	and (
		business_type = #{pd.business_type}
	)



	and (
		p.purchase_group_id = #{pd.purchase_group_id}
	)


	and (
	p.purchase_group_id in 
		${pd.purchase_group_ids}
	)


order by p.create_time desc
