mapper.insert:
insert into
( direct_sales_code, type, status, store_sn, store_name, remark, auditby, audit_time, createby, create_time, updateby, update_time ) values (
)

mapper.select:
select

from 

where 1=1

and store_sn in 

  #{pd.my_store[${index}]}



	and direct_sales_code = #{pd.sh_direct_sales_code}


	and type = #{pd.sh_type}


	and status = #{pd.sh_status}


	and 
	(
		store_sn = #{pd.store_sn}
	)


	and 
	(
			store_sn LIKE CONCAT('%', #{pd.store_sn_name},'%')
			 or 
			store_name LIKE CONCAT('%', #{pd.store_sn_name},'%') 
		)

order by create_time desc
