mapper.select:
select 

from 

where 1=1

		and warehouse_code = #{warehouse_code}


		and goods_sn = #{goods_sn}

select w.warehouse_code, w.goods_id, w.goods_sn, w.goods_name, w.actual_quantity, w.available_quantity, w.unit_name, w.create_user, w.create_time, w.update_user, w.update_time, w.warestock_id, d.warehouse_name from w left join d on w.warehouse_code=d.warehouse_code where 1=1
and (
w.warehouse_code LIKE CONCAT(CONCAT('%', #{pd.keywords}),'%') or w.goods_sn LIKE CONCAT(CONCAT('%', #{pd.keywords}),'%') or w.goods_name LIKE CONCAT(CONCAT('%', #{pd.keywords}),'%') or w.unit_name LIKE CONCAT(CONCAT('%', #{pd.keywords}),'%') or d.warehouse_name LIKE CONCAT(CONCAT('%', #{pd.keywords}),'%') )
and w.create_time >= #{pd.lastStart}
and w.create_time <= #{pd.lastEnd}
