mapper.insert:
insert into
( bonus_id, bonus_type, bonus_name, bonus_desc, bonus_image, bonus_amount, receive_count, bonus_number, use_range, min_goods_amount, start_time, end_time, is_enable, is_delete, add_time, recharge_card_id, recharge_chance, exchange_points, exchange_goods_number ) values (
)

mapper.update:
	update

	set  bonus_id = #{bonus_id}

		, bonus_amount = #{bonus_amount}


		, min_goods_amount = #{min_goods_amount}


		, bonus_number = #{bonus_number}


		 ,start_time = #{start_time}


		, end_time  = #{end_time }


		, is_enable  = #{is_enable }

 where 
	bonus_id = #{bonus_id}

mapper.select:
SELECT
	a.*,
	( SELECT count( * ) FROM dl_user_bonus b WHERE b.bonus_id = a.bonus_id ) AS receive_quantity,
	( SELECT count( * ) FROM dl_user_bonus b WHERE b.bonus_status = 1 AND b.bonus_id = a.bonus_id ) AS use_count 
FROM
	dl_activity_bonus a
WHERE 1=1

	and  a.bonus_amount   > #{pd.amountStart }


	and a.bonus_amount  < #{pd.amountEnd }


	and a.is_enable = #{pd.is_enable }
