mapper.insert:
insert into
( article_id, title, cat_id, content, keywords, jurisdiction, article_thumb, add_time, is_comment, click_number, is_show, user_id, status, link, source, summary, extend_cat, is_recommend, author, match_id, related_team, list_style, is_original, is_delete, is_stick, stick_time, price, level, article_pv, article_uv, label_defaults, video_url ) values (
)

mapper.update:
update

set 

			status = #{status} 



			,

			is_stick = #{is_stick}

where 
article_id = #{article_id}

mapper.select:
select
from
where 1=1
and title LIKE CONCAT(CONCAT('%', #{pd.title}),'%')
and author LIKE CONCAT(CONCAT('%', #{pd.author}),'%')
and article_id LIKE CONCAT(CONCAT('%', #{pd.article_id}),'%')
and status = #{pd.status}
and match_id = #{pd.match_id}
and add_time > #{pd.lastStart1 }
and add_time < #{pd.lastEnd1 }
order by is_stick desc , status asc
