insert into
( option_id, channel_id, distributor_id, user_id, user_name, id_card_num, true_name, mobile, operation_node, status, option_amount, option_time, source ) values (
)

mapper.select:
select log.*,channel.channel_name from dl_channel channel left join ( SELECT con.add_time as option_time , con.user_name , con.mobile , chl.channel_name as channelname, chl.channel_id, cdt.mobile as dis_name, '10' as operation_node, '' as option_amount, ''as option_amount_chl, '' as option_amount_cdt FROM dl_channel_consumer con left join dl_channel_distributor cdt on con.channel_distributor_id = cdt.channel_distributor_id left join dl_channel chl on cdt.channel_id = chl.channel_id where !ISNULL(con.user_id) and ISNULL(con.frist_login_time)
and 0=1
and chl.channel_id = #{pd.channel_id}
and con.add_time >= #{pd.lastStart1 }
and con.add_time <= #{pd.lastEnd1 }
and ( chl.channel_name LIKE CONCAT(CONCAT('%', #{pd.keywords}),'%') or cdt.mobile LIKE CONCAT(CONCAT('%', #{pd.keywords}),'%') )
UNION SELECT opt.option_time as option_time, opt.user_name, opt.mobile, chl.channel_name as channelname, chl.channel_id, cdt.mobile as dis_name, opt.operation_node, opt.option_amount, FORMAT(opt.option_amount*0.01,2) as option_amount_chl, FORMAT(opt.option_amount*0.02,2) as option_amount_cdt
FROM dl_channel_option_log opt left join dl_channel chl on opt.channel_id = chl.channel_id left join dl_channel_distributor cdt on opt.distributor_id = cdt.channel_distributor_id WHERE 1 = 1
and chl.channel_id = #{pd.channel_id}
and opt.operation_node = #{pd.operation_node}
and opt.option_time >= #{pd.lastStart1 }
and opt.option_time <= #{pd.lastEnd1 }
and ( chl.channel_name LIKE CONCAT(CONCAT('%', #{pd.keywords}),'%') or cdt.mobile LIKE CONCAT(CONCAT('%', #{pd.keywords}),'%') )
) log on channel.channel_id = log.channel_id where 1=1
and channel.channel_id = #{pd.channel_id}
and channel.channel_name LIKE CONCAT(CONCAT('%', #{pd.keywords}),'%')
and log.operation_node = #{pd.operation_node}
order by log.option_time desc

select COALESCE(count(user_id),0) as count, '0' as operation_node from dl_channel_consumer where !ISNULL(user_id)
and add_time > #{pd.lastStart1 }
and add_time < #{pd.lastEnd1 }
UNION SELECT COALESCE(count(DISTINCT user_id),0) as count, operation_node from dl_channel_option_log where 1=1
and option_time > #{pd.lastStart1 }
and option_time < #{pd.lastEnd1 }
GROUP BY operation_node UNION SELECT COALESCE(sum(option_amount),0) as count, '111' as operation_node from dl_channel_option_log where 1=1
and option_time > #{pd.lastStart1 }
and option_time < #{pd.lastEnd1 }
