mapper.select:
select count(1) as ORDER_NUMS,IFNULL(sum(ticket_amount),0) as
TICKENT_AMOUNT from dl_order dl
left join 
	dl_log_operation log
		on dl.order_sn=log.order_sn
WHERE log.type=2 

	and log.phone = #{store_id}


	and log.add_time >= #{lastStart1 }


	and log.add_time <= #{lastEnd1 }

SELECT IFNULL(dlc.lottery_name,'') lottery_name, IFNULL(dlc.lottery_img,'') lottery_img, log.op_type print_lottery_status, IFNULL(log.fail_msg,'') fail_msg, IFNULL(log.add_time,'') ticket_time, IFNULL(log.phone,'') store_id,
FROM dl_order dl left join dl_lottery_classify dlc on dl.lottery_classify_id=dlc.lottery_classify_id left join dl_log_operation log on dl.order_sn=log.order_sn WHERE log.type=2
and log.phone = #{pd.store_id}
and dl.order_status in(3,4)
and dl.order_status in(6,7,9)
and log.op_type=1
and log.op_type=2
and log.add_time >= #{pd.lastStart1 }
and log.add_time <= #{pd.lastEnd1 }
order by log.add_time desc

SELECT IFNULL(dlc.lottery_name,'') lottery_name, IFNULL(dlc.lottery_img,'') lottery_img, log.op_type print_lottery_status, IFNULL(log.fail_msg,'') fail_msg, IFNULL(log.add_time,'') ticket_time, IFNULL(log.phone,'') store_id,
FROM dl_order dl left join dl_lottery_classify dlc on dl.lottery_classify_id=dlc.lottery_classify_id left join dl_log_operation log on dl.order_sn=log.order_sn WHERE log.type=2
and dl.order_status in(3,4)
and dl.order_status in(6,7,9)
and log.phone = #{store_id}
and log.op_type=1
and log.op_type=2
and log.add_time >= #{lastStart1 }
and log.add_time <= #{lastEnd1 }
order by log.add_time desc

mapper.update:
update dl_order set
auditor_id=#{auditor_id },
check_status=#{check_status },
check_remark=#{check_remark },
check_time=#{check_time }
where order_id=#{order_id }
