)

mapper.update:
update

set 
	pay_time = #{pay_time},
	match_time = #{match_time}
where 
order_id = #{order_id}

mapper.select:
SELECT o.order_sn, u.user_id, u.user_name, u.mobile, l.lottery_name, o.order_status, o.pay_name, o.ticket_amount, o.winning_money, o.bonus, o.pay_time FROM dl_order o LEFT JOIN dl_lottery_classify l ON o.lottery_classify_id = l.lottery_classify_id LEFT JOIN dl_user u ON o.user_id = u.user_id WHERE o.order_status = 7
and o.order_sn LIKE CONCAT(CONCAT('%', #{pd.order_sn}),'%')
and u.mobile LIKE CONCAT(CONCAT('%', #{pd.mobile}),'%')
and u.user_name LIKE CONCAT(CONCAT('%', #{pd.user_name}),'%')
and o.pay_time > #{pd.lastStart }
and o.pay_time < #{pd.lastEnd1 }
and o.winning_money > #{pd.amountStart }
and o.winning_money < #{pd.amountEnd }

SELECT o.order_id, o.order_sn, u.user_id, u.user_name, u.mobile, l.lottery_name, l.lottery_classify_id, o.order_status, o.pay_name, o.surplus, o.ticket_amount, o.winning_money, o.bonus, o.add_time, o.pay_time FROM dl_order o LEFT JOIN dl_lottery_classify l ON o.lottery_classify_id = l.lottery_classify_id LEFT JOIN dl_user u ON o.user_id = u.user_id WHERE 1 = 1
and o.order_sn LIKE CONCAT(CONCAT('%', #{pd.order_sn}),'%')
and u.mobile LIKE CONCAT(CONCAT('%', #{pd.mobile}),'%')
and u.user_name LIKE CONCAT(CONCAT('%', #{pd.user_name}),'%')
and o.add_time > #{pd.lastStart1 }
and o.add_time < #{pd.lastEnd1 }
and o.winning_money > #{pd.amountStart }
and o.winning_money < #{pd.amountEnd }
and o.order_status = #{pd.order_status }
and o.order_status != 0
and l.lottery_classify_id = #{pd.lottery_classify_id }
order by o.add_time desc
