SELECT l.lottery_name, s.name, o.order_id order_id, o.order_sn order_sn, o.user_id user_id, o.money_paid money_paid, u.user_name user_name, u.mobile mobile, o.order_status order_status, o.pay_name pay_name, o.ticket_amount ticket_amount, o.winning_money winning_money, o.bonus bonus, o.surplus surplus, o.third_party_paid third_party_paid, o.add_time add_time, o.pay_time pay_time, o.pay_status pay_status, o.app_code_name app_code_name, a.order_status mo_status, a.operation_time mo_add_time, o.store_id, a.admin_name FROM dl_order o LEFT JOIN dl_user u ON o.user_id = u.user_id LEFT JOIN dl_artifi_print_lottery a ON o.order_sn = a.order_sn LEFT JOIN dl_lottery_classify l ON l.lottery_classify_id = o.lottery_classify_id LEFT JOIN dl_store s ON s.id = o.store_id WHERE o.add_time > 1541520000 and o.is_delete!=1
