SELECT o.order_sn, o.user_id, lpc.play_name, GROUP_CONCAT(d.match_id) as match_id , d.changci, o.pass_type, o.cathectic , o.ticket_amount , o.order_status , o.winning_money , o.add_time , o.award_time FROM dl_order o LEFT JOIN dl_order_detail d on o.order_id = d.order_id LEFT JOIN dl_lottery_play_classify lpc on o.lottery_play_classify_id=lpc.lottery_play_classify_id where o.add_time > #{pd.lastStart1} and o.add_time < #{pd.lastEnd1} and o.is_delete!=1
and o.order_sn = #{pd.orderSN }
and o.user_id = #{pd.userID }
and o.order_status = #{pd.statusCheck }
and o.ticket_amount >= #{pd.ticketStart }
and o.ticket_amount <= #{pd.ticketEnd }
and o.winning_money >= #{pd.winningStart }
and o.winning_money <= #{pd.winningEnd }
GROUP BY order_sn ORDER BY add_time

select COALESCE(count(DISTINCT o.user_id),0) as userCount, COALESCE(count(DISTINCT o.order_id),0) as orderCount, COALESCE(sum(o.ticket_amount),0) as amountSum, COALESCE(sum(o.winning_money),0) as winningSum, COALESCE(sum(o.money_paid),0) as paidSum, COALESCE(sum(o.surplus),0) as surplusSum, COALESCE(sum(o.third_party_paid),0) as thirdSum, COALESCE(sum(o.bonus),0) as bonusSum from dl_order o left join dl_user u on o.user_id = u.user_id where o.order_status in ( '3', '4', '5', '6', '7' ) and o.is_delete!=1
and DATE_FORMAT( FROM_UNIXTIME( o.add_time ), "%Y-%m-%d" ) >= #{pd.lastStart1 }
and DATE_FORMAT( FROM_UNIXTIME( o.add_time ), "%Y-%m-%d" ) <= #{pd.lastEnd1 }
and u.user_id=#{pd.UserID }

SELECT COALESCE(sum(ticket_amount),0) as amountSum, COALESCE(sum(money_paid),0) as paidSum, order_status as status FROM dl_order where is_delete!=1
and DATE_FORMAT( FROM_UNIXTIME( add_time ), "%Y-%m-%d" ) >= #{pd.lastStart1 }
and DATE_FORMAT( FROM_UNIXTIME( add_time ), "%Y-%m-%d" ) <= #{pd.lastEnd1 }
GROUP BY order_status

