mapper.select:
SELECT user_id, money_paid, ticket_time, DATE_FORMAT( FROM_UNIXTIME( ticket_time ), '%Y-%m-%d %H:%i' ) AS ticket_time_str FROM dl_order WHERE order_status IN ( 3, 4, 5, 6, 7 )
and add_time > #{pd.lastStart1 }
and add_time < #{pd.lastEnd1 }

select
from
where order_sn = #{order_sn}

select
from
where order_id = #{order_id}

SELECT o.order_id, o.order_sn, u.user_id, u.user_name, u.mobile, l.lottery_name, o.order_status, o.pay_name, o.ticket_amount, o.third_party_paid, o.winning_money, o.bonus, o.surplus, o.pay_time FROM dl_order o LEFT JOIN dl_lottery_classify l ON o.lottery_classify_id = l.lottery_classify_id LEFT JOIN dl_user u ON o.user_id = u.user_id WHERE o.order_status = 7
and o.order_sn LIKE CONCAT(CONCAT('%', #{pd.order_sn}),'%')
and u.mobile LIKE CONCAT(CONCAT('%', #{pd.mobile}),'%')
and u.user_name LIKE CONCAT(CONCAT('%', #{pd.user_name}),'%')
and o.pay_time >= #{pd.lastStart }
and o.pay_time <= #{pd.lastEnd1 }
and o.winning_money >= #{pd.amountStart }
and o.winning_money <= #{pd.amountEnd }
order by pay_time desc

SELECT o.order_id, o.order_sn, u.user_id, u.user_name, u.mobile, l.lottery_name, l.lottery_classify_id, o.order_status, o.pay_name, o.ticket_amount, o.winning_money, o.bonus, o.surplus, o.third_party_paid, o.add_time, o.pay_time, o.pay_status FROM dl_order o LEFT JOIN dl_lottery_classify l ON o.lottery_classify_id = l.lottery_classify_id LEFT JOIN dl_user u ON o.user_id = u.user_id WHERE 1 = 1
and o.order_sn LIKE CONCAT(CONCAT('%', #{pd.order_sn}),'%')
and u.mobile LIKE CONCAT(CONCAT('%', #{pd.mobile}),'%')
and u.user_name LIKE CONCAT(CONCAT('%', #{pd.user_name}),'%')
and o.add_time > #{pd.lastStart1 }
and o.add_time < #{pd.lastEnd1 }
and o.winning_money > #{pd.amountStart }
and o.winning_money < #{pd.amountEnd }
and o.order_status = #{pd.order_status }
and o.order_status != 8 and o.order_status != 0
and l.lottery_classify_id = #{pd.lottery_classify_id }
order by o.add_time desc

SELECT o.order_id order_id, o.order_sn order_sn, u.user_id user_id, u.user_name user_name, u.mobile mobile, o.order_status order_status, o.pay_name pay_name, o.ticket_amount ticket_amount, o.winning_money winning_money, o.bonus bonus, o.surplus surplus, o.third_party_paid third_party_paid, o.add_time add_time, o.pay_time pay_time, o.pay_status pay_status, a.order_status mo_status, a.operation_time mo_add_time FROM dl_order o LEFT JOIN dl_user u ON o.user_id = u.user_id LEFT JOIN dl_artifi_print_lottery a ON o.order_sn = a.order_sn WHERE o.add_time > 1540981190
and o.order_sn LIKE CONCAT(CONCAT('%', #{pd.order_sn}),'%')
and u.mobile LIKE CONCAT(CONCAT('%', #{pd.mobile}),'%')
and u.user_name LIKE CONCAT(CONCAT('%', #{pd.user_name}),'%')
and o.add_time > #{pd.lastStart1 }
and o.add_time < #{pd.lastEnd1 }
and o.winning_money > #{pd.amountStart }
and o.winning_money < #{pd.amountEnd }
and a.order_status is null
and a.order_status = #{pd.mo_status }
and o.order_status IN ( 3, 4, 5, 6, 7 )
and o.order_status = #{pd.order_status }
and o.order_status <> 8
order by o.add_time desc

select o.order_id, o.order_sn, u.user_id, u.user_name, u.mobile, l.lottery_name, l.lottery_classify_id, o.order_status, o.pay_name, o.ticket_amount, o.winning_money, o.bonus, o.surplus, o.third_party_paid, o.add_time, o.pay_time, p.pay_order_sn FROM dl_order o LEFT JOIN dl_lottery_classify l ON o.lottery_classify_id = l.lottery_classify_id LEFT JOIN dl_user u ON o.user_id = u.user_id LEFT JOIN dl_pay_log p ON o.order_sn = p.order_sn
WHERE 1 = 1
and o.add_time > #{lastStart1 }
and o.add_time < #{lastEnd1 }
and o.order_status = #{order_status }

select o.order_id, o.order_sn, u.user_id, u.user_name, u.mobile, o.order_status, o.pay_name, o.ticket_amount, o.winning_money, o.bonus, o.surplus, o.third_party_paid, o.add_time, o.pay_time, a.order_status mo_status, a.add_time mo_add_time FROM dl_order o LEFT JOIN dl_user u ON o.user_id = u.user_id LEFT JOIN dl_artifi_print_lottery a ON o.order_sn = a.order_sn WHERE o.add_time > 1540981190
and o.order_sn = #{orderSn }
and a.order_status is null
and a.order_status = #{moStatus }
and o.order_status IN ( 3, 4, 5, 6, 7 )
and o.order_status = #{orderStatus }

 SELECT  user_id from dl_order
	where order_status in ('3','4','5','6','7') GROUP BY user_id 
	HAVING min(add_time) >= #{pd.lastStart1} and  min(add_time) <= #{pd.lastEnd1}  ;

SELECT  o.order_sn, 
		o.user_id,
		lpc.play_name,
		GROUP_CONCAT(d.match_id) as match_id  ,
		d.changci,
		o.pass_type,
		o.cathectic  ,
		o.ticket_amount ,
		o.order_status ,
		o.winning_money ,
		o.add_time ,
		o.award_time  
FROM dl_order o LEFT JOIN dl_order_detail d on o.order_id = d.order_id 
				LEFT JOIN dl_lottery_play_classify lpc on o.lottery_play_classify_id=lpc.lottery_play_classify_id
where o.add_time > #{pd.lastStart1} and  o.add_time < #{pd.lastEnd1}

		and o.order_sn = #{pd.orderSN }


	 	and o.user_id = #{pd.userID }


	 	and o.order_status = #{pd.statusCheck }


	 	and o.ticket_amount >= #{pd.ticketStart }


	 	and o.ticket_amount <= #{pd.ticketEnd }


	 	and o.winning_money >= #{pd.winningStart }


	 	and o.winning_money <= #{pd.winningEnd }

GROUP BY order_sn
ORDER BY add_time

select COALESCE(count(DISTINCT o.user_id),0) as userCount, COALESCE(count(DISTINCT o.order_id),0) as orderCount, COALESCE(sum(o.ticket_amount),0) as amountSum, COALESCE(sum(o.winning_money),0) as winningSum, COALESCE(sum(o.money_paid),0) as paidSum, COALESCE(sum(o.surplus),0) as surplusSum, COALESCE(sum(o.third_party_paid),0) as thirdSum, COALESCE(sum(o.bonus),0) as bonusSum from dl_order o left join dl_user u on o.user_id = u.user_id where o.order_status in ( '3', '4', '5', '6', '7' )
and DATE_FORMAT( FROM_UNIXTIME( o.add_time ), "%Y-%m-%d" ) >= #{pd.lastStart1 }
and DATE_FORMAT( FROM_UNIXTIME( o.add_time ), "%Y-%m-%d" ) <= #{pd.lastEnd1 }
and u.user_id=#{pd.UserID }

SELECT COALESCE(sum(ticket_amount),0) as amountSum, COALESCE(sum(money_paid),0) as paidSum, order_status as status FROM dl_order where 1=1
and DATE_FORMAT( FROM_UNIXTIME( add_time ), "%Y-%m-%d" ) >= #{pd.lastStart1 }
and DATE_FORMAT( FROM_UNIXTIME( add_time ), "%Y-%m-%d" ) <= #{pd.lastEnd1 }
GROUP BY order_status
