)

mapper.update:
update

set 
	pay_time = #{pay_time},
	match_time = #{match_time}
where 
order_id = #{order_id}

mapper.select:
SELECT user_id, money_paid, ticket_time, DATE_FORMAT( FROM_UNIXTIME( ticket_time ), '%Y-%m-%d %H:%i' ) AS ticket_time_str FROM dl_order WHERE order_status IN ( 3, 4, 5, 6, 7 )
and add_time > #{pd.lastStart1 }
and add_time < #{pd.lastEnd1 }

SELECT o.order_sn, u.user_id, u.user_name, u.mobile, l.lottery_name, o.order_status, o.pay_name, o.ticket_amount, o.third_party_paid, o.winning_money, o.bonus, o.surplus, o.pay_time FROM dl_order o LEFT JOIN dl_lottery_classify l ON o.lottery_classify_id = l.lottery_classify_id LEFT JOIN dl_user u ON o.user_id = u.user_id WHERE o.order_status = 7
and o.order_sn LIKE CONCAT(CONCAT('%', #{pd.order_sn}),'%')
and u.mobile LIKE CONCAT(CONCAT('%', #{pd.mobile}),'%')
and u.user_name LIKE CONCAT(CONCAT('%', #{pd.user_name}),'%')
and o.pay_time > #{pd.lastStart }
and o.pay_time < #{pd.lastEnd1 }
and o.winning_money > #{pd.amountStart }
and o.winning_money < #{pd.amountEnd }
order by pay_time desc

SELECT o.order_id, o.order_sn, u.user_id, u.user_name, u.mobile, l.lottery_name, l.lottery_classify_id, o.order_status, o.pay_name, o.ticket_amount, o.winning_money, o.bonus, o.surplus, o.third_party_paid, o.add_time, o.pay_time FROM dl_order o LEFT JOIN dl_lottery_classify l ON o.lottery_classify_id = l.lottery_classify_id LEFT JOIN dl_user u ON o.user_id = u.user_id WHERE 1 = 1
and o.order_sn LIKE CONCAT(CONCAT('%', #{pd.order_sn}),'%')
and u.mobile LIKE CONCAT(CONCAT('%', #{pd.mobile}),'%')
and u.user_name LIKE CONCAT(CONCAT('%', #{pd.user_name}),'%')
and o.add_time > #{pd.lastStart1 }
and o.add_time < #{pd.lastEnd1 }
and o.winning_money > #{pd.amountStart }
and o.winning_money < #{pd.amountEnd }
and o.order_status = #{pd.order_status }
and o.order_status != 0
and l.lottery_classify_id = #{pd.lottery_classify_id }
order by o.add_time desc

SELECT 	COALESCE(count(DISTINCT user_id),0) as count,
		COALESCE(sum(ticket_amount),0) as amount
from dl_order   
where order_sn not in 
		(SELECT 	order_sn from dl_order 
		where order_status in ('3','4','5','6','7') GROUP BY user_id 
		HAVING min(add_time) > #{pd.lastStart1} and  min(add_time) < #{pd.lastEnd1}
		)
	and user_id not in (
		SELECT  user_id  from dl_order 
		where add_time < #{pd.lastStart1} 
				and order_status in ('3','4','5','6','7')
		GROUP BY user_id  having count(user_id)>1
		) 
	and   order_status in ('3','4','5','6','7')
	and add_time  > #{pd.lastStart1} and  add_time < #{pd.lastEnd1}
