mapper.select:
select 

from 

where 1 = 1 
and	user_id = #{user_id}
and store_id = #{store_id}

select
from
where 1=1
and ( user_id LIKE CONCAT(CONCAT('%', #{pd.keywords}),'%')
)
and process_type = #{pd.process_type}

SELECT o.order_sn as order_sn, o.amount as amount, a.pay_time as pay_time, a.award_time as award_time, a.ticket_amount as ticket_amount, u.user_name as user_name, u.mobile as mobile, c.lottery_name as lottery_name FROM dl_user_account o LEFT JOIN dl_order a ON o.order_sn = a.order_sn LEFT JOIN dl_user u ON o.user_id = u.user_id LEFT JOIN dl_lottery_classify c ON a.lottery_classify_id = c.lottery_classify_id WHERE o.process_type = 1
AND u.user_name LIKE CONCAT(CONCAT('%', #{pd.user_name}),'%')
AND u.mobile LIKE CONCAT(CONCAT('%', #{pd.mobile}),'%')
AND o.order_sn LIKE CONCAT(CONCAT('%', #{pd.order_sn}),'%')
AND a.pay_time >= #{pd.lastStart1}
AND a.pay_time <= #{pd.lastEnd1}
AND o.amount >= #{pd.amountStart}
AND o.amount <= #{pd.amountEnd}
Order By a.pay_time DESC

 SELECT
 	COALESCE(count(DISTINCT ua.user_id),0) as userCount,
 	COALESCE(sum(ua.amount),0) as amountSum,
 	ua.process_type
FROM
	dl_user_account ua left join dl_user us on ua.user_id=us.user_id
WHERE 1 = 1 and ua.status=1 

			and ua.process_type = #{pd.process_type}


				AND ua.add_time >= #{pd.lastStart1} 


				AND ua.add_time <= #{pd.lastEnd1} 


				AND us.device_channel =#{pd.device_channel} 

GROUP BY ua.process_type;
