mapper.update:
update dl_user set

    user_money = user_money + #{money}


    user_money_limit = user_money_limit + #{money_limit}

where user_id = #{user_id}

update dl_user set

    is_old = #{is_old}

where user_id = #{user_id}

update dl_user set

    user_money = user_money - #{money}


    user_money_limit = user_money_limit - #{money_limit}

where user_id = #{user_id}
