)

mapper.select:
		SELECT COUNT(DISTINCT u.user_id) as count
 		from dl_user u left join dl_user_login_log l on u.user_id = l.user_id 
		where 1=1


				AND DATE_FORMAT(ADDDATE(FROM_UNIXTIME(u.reg_time),#{pd.dayStart}),"%Y-%m-%d") <= DATE_FORMAT(FROM_UNIXTIME(l.login_time),"%Y-%m-%d")


				AND DATE_FORMAT(ADDDATE(FROM_UNIXTIME(u.reg_time),#{pd.dayEnd}),"%Y-%m-%d") >= DATE_FORMAT(FROM_UNIXTIME(l.login_time),"%Y-%m-%d")

				AND DATE_FORMAT(FROM_UNIXTIME(u.reg_time),"%Y-%m-%d")=#{pd.regTime}


					AND u.reg_time > #{pd.lastStart1} 
					AND DATE_FORMAT(ADDDATE(FROM_UNIXTIME(#{pd.lastStart1}),#{pd.dayStart}),"%Y-%m-%d") <= DATE_FORMAT(FROM_UNIXTIME(l.login_time),"%Y-%m-%d")
					AND DATE_FORMAT(ADDDATE(FROM_UNIXTIME(#{pd.lastStart1}),#{pd.dayEnd}),"%Y-%m-%d") >= DATE_FORMAT(FROM_UNIXTIME(l.login_time),"%Y-%m-%d")


					AND u.reg_time < #{pd.lastEnd1} 


					AND u.device_channel = #{pd.device_channel}

SELECT u.user_id, u.user_money, u.user_money_limit, u.user_name, u.mobile, u.nickname, u.reg_time, u.reg_ip, u.last_time, u_r.real_name, u_r.id_code, u.user_status, u.reg_from, p.channel_name AS phone_channel, u.device_channel, log.brand, u.is_business FROM dl_user u LEFT JOIN (select * from dl_user_real where is_delete = 0) u_r ON u_r.user_id = u.user_id LEFT JOIN dl_phone_channel p ON u.device_channel = p.channel LEFT JOIN ( SELECT user_id, brand FROM dl_user_login_log WHERE brand != '' GROUP BY user_id HAVING min( login_time ) ) log ON log.user_id = u.user_id WHERE 1 = 1
and u_r.real_name LIKE CONCAT(CONCAT('%', #{pd.real_name}),'%')
and u_r.id_code LIKE CONCAT(CONCAT('%', #{pd.id_code}),'%')
and u.mobile LIKE CONCAT(CONCAT('%', #{pd.mobile}),'%')
and u.nickname LIKE CONCAT(CONCAT('%', #{pd.nickname}),'%')
and u.reg_time > #{pd.lastStart1 }
and u.reg_time < #{pd.lastEnd1 }
and u.user_status = #{pd.user_status}
and u.is_real = #{pd.is_real}
order by u.reg_time DESC

select dl_user.user_id, dl_user.user_money, dl_user.user_money_limit, user_name, mobile, nickname, reg_time, last_time, real_name, id_code, dl_user.user_status, reg_from, ph.channel_name as mobile_supplier, pc.channel_name as device_channel, sex*1 as sex from
left join dl_user_real on dl_user_real.user_id = dl_user.user_id left join dl_phone_channel ph on dl_user.mobile_supplier = ph.id left join dl_phone_channel pc on pc.channel = dl_user.device_channel where 1=1 and dl_user_real.is_delete = 0
and ( dl_user.user_id LIKE CONCAT(CONCAT('%', #{pd.keywords}),'%') or mobile LIKE CONCAT(CONCAT('%', #{pd.keywords}),'%') or nickname LIKE CONCAT(CONCAT('%', #{pd.keywords}),'%')
)
AND reg_time > #{pd.lastStart1}
AND reg_time < #{pd.lastEnd1}
and ( user_status = #{pd.name_chosen} )
and ( is_real = 1 )
and ( is_real = 0 )
order by dl_user.reg_time DESC

