mapper.select:
SELECT SUM(user_account.recharge_card_real_value) as recharge_card_real_value
FROM `dl_user_account` user_account

WHERE 1 = 1

and user_account.user_id = #{user_id}


AND user_account.store_id = #{store_id}

SELECT user.`mobile`, store.`name`, user_account.* FROM `dl_user_account` user_account, `dl_user` user, `dl_store` store WHERE user_account.`user_id` = user.user_id AND user_account.`store_id` = store.`id`
and user_account.user_id LIKE CONCAT(CONCAT('%', #{pd.user_id}),'%')
and user_account.user_id LIKE CONCAT(CONCAT('%', #{pd.user_id}),'%')
and user_account.store_id LIKE CONCAT(CONCAT('%', #{pd.store_id}),'%')
and store.name LIKE CONCAT(CONCAT('%', #{pd.store_name}),'%')
AND user_account.`add_time` <= #{pd.end_add_time}
AND user_account.`add_time` >= #{pd.start_add_time}
AND user_account.`process_type` = #{pd.process_type}
and (user_account.`process_type` = 2 or user_account.`process_type` = 4 or user_account.`process_type` = 8 or user_account.`process_type` = 9)
order by user_account.add_time desc

SELECT user.`mobile`, store.`name`, user_account.* FROM `dl_user_account` user_account, `dl_user` user, `dl_store` store WHERE user_account.`user_id` = user.user_id AND user_account.`store_id` = store.`id` and (user_account.`process_type` = 2 or user_account.`process_type` = 4 or user_account.`process_type` = 8 or user_account.`process_type` = 9)
and user_account.user_id LIKE CONCAT(CONCAT('%', #{user_id}),'%')
and user_account.store_id LIKE CONCAT(CONCAT('%', #{store_id}),'%')
and store.name LIKE CONCAT(CONCAT('%', #{store_name}),'%')
AND user_account.`add_time` <= #{end_add_time}
AND user_account.`add_time` >= #{start_add_time}
AND user_account.`process_type` = #{process_type}
and (user_account.`process_type` = 2 or user_account.`process_type` = 4 or user_account.`process_type` = 8 or user_account.`process_type` = 9)
order by user_account.add_time desc
